import React from 'react';

const Layout = ({ children }) => {
  return (
    <div className="bg-[#f4f4f4] text-[#333]">
      {children}
    </div>
  );
};
const Header = ({ children }) => {
  return (
    <div className="bg-white text-center border-b border-solid border-gray-300 ">
      <h1 className="text-3xl font-bold">
      {children}
      </h1>

    </div>
  );
};
const Nav = ({ children }) => {
  return (
    <div className="py-4 px-6 text-base font-medium">
      <ul className="flex space-x-3 justify-center">
        {children}
      </ul>
    </div>
  );
};
const NavItem = ({ href, isActive, children }) => {
  return (
    <li>
      <a
        href={href}
        className={`block px-3 py-2 rounded-md ${isActive ? 'bg-white text-black' : 'bg-slate-50'}`}
      >
        {children}
      </a>
    </li>
  );
};
const PresentationCard = ({ children }) => {
  return (
<div className="relative flex flex-grow flex-shrink basis-[200px] m-1 border border-gray-300 rounded-lg overflow-hidden transition-shadow transition-transform w-full max-w-[310px] shadow hover:translate-y-[-5px] hover:shadow-lg">
      {children}
    </div>
  );
};
const ProjectCardItem = ({children }) => {
  return (
  <div class="absolute bottom-0 left-0 w-full bg-white/[.8] text-[#333] text-center z-20">
  {children}
  </div>
  );
};
const Footer = ({ children }) => {
  return (
    <div className="py-1 font-bold text-center border-t border-solid border-gray-300 bg-white text-[#333]">
      {children}
    </div>
  );
};
export { Layout, Header, Nav, NavItem, PresentationCard, ProjectCardItem, Footer};
