import React, { useState, useEffect } from 'react';
import ROUTES from './routes';
import { Layout, Header, Nav, NavItem, Footer, Box } from './style.js';
import emailjs from 'emailjs-com';

function CONTACT() {
  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');
  const [notification, setNotification] = useState(false);

  useEffect(() => {
    emailjs.init("user_fCdwqk3H56TaMSsD"); // Remplacez par votre clé publique EmailJS
  }, []);

  const handleSubmit = (event) => {
    event.preventDefault();

    const formData = {
      from_name: name,
      from_email: email,
      message: message,
    };

    emailjs.send("service_lbrk2uf", "template_7t0uwrt", formData)
      .then(response => {
        console.log('SUCCESS!', response.status, response.text);
        setNotification(true);
        setTimeout(() => setNotification(false), 4000);
        setName('');
        setEmail('');
        setMessage('');
      })
      .catch(error => {
        console.log('FAILED...', error);
        alert('Erreur lors de l\'envoi du message: ' + error);
      });
  };

  return (
    <Layout>
      {notification && <div id="notification" className="notification">Message bien envoyé</div>}
      <Header>
        <h1>Contactez-moi</h1>
        <div className="hamburger">
          <div></div>
          <div></div>
          <div></div>
        </div>
        <Nav>
          <NavItem to={ROUTES.HOME} isActive>Accueil</NavItem>
          <NavItem to={ROUTES.PROJETS} isActive>Projets</NavItem>
          <NavItem to={ROUTES.ABOUT} isActive>À propos</NavItem>
          <NavItem to={ROUTES.CONTACT} isActive>Contact</NavItem>
        </Nav>
      </Header>
      <Box>
        <form id="contact-form" onSubmit={handleSubmit}>
          <label htmlFor="name">Nom :</label>
          <input type="text" id="name" name="name" value={name} onChange={(e) => setName(e.target.value)} required />

          <label htmlFor="email">Email :</label>
          <input type="email" id="email" name="email" value={email} onChange={(e) => setEmail(e.target.value)} required />

          <label htmlFor="message">Message :</label>
          <textarea id="message" name="message" rows="5" value={message} onChange={(e) => setMessage(e.target.value)} required></textarea>

          <button className='descBtn' type="submit">Envoyer</button>
        </form>
      </Box>
      <Footer>
        <p>&copy; 2024 Fabro Ludovic Portfolio</p>
      </Footer>
    </Layout>
  );
}

export default CONTACT;
