import React, { useState } from 'react';
import ROUTES from './routes';

function CONTACT() {

    const [name, setName] = useState('')
    const [email, setEmail] = useState('')
    const [message, setMessage ] = useState('')

// function handleSubmit(event){
//     event.preventDefault();
// }
  return (
<body>
  <div id="notification" class="notification">Message bien envoyé</div>
  <header>
    <h1>Contactez-moi</h1>
    <div class="hamburger">
      <div></div>
      <div></div>
      <div></div>
    </div>
    <nav>
      <ul>
          <li><a href={ROUTES.HOME}>Accueil</a></li>
          <li><a href={ROUTES.PROJETS}>Projets</a></li>
          <li><a href={ROUTES.ABOUT}>À propos</a></li>
          <li><a href={ROUTES.CONTACT}>Contact</a></li>
      </ul>
    </nav>
  </header>
  <main>
    <section class="contact-box">
      <form id="contact-form" method="POST">
        <label for="name">Nom :</label>
        <input type="text" id="name" name="name" value={name} onChange={(e) =>setName(e.target.value)} required/>

        <label for="email">Email :</label>
        <input type="email" id="email" name="email" value={email} onChange={(e) =>setEmail(e.target.value)} required/>

        <label for="message">Message :</label>
        <textarea id="message" name="message" rows="5" value={message} onChange={(e) =>setMessage(e.target.value)} required></textarea>

        <button type="submit">Envoyer</button>
      </form>
    </section>
  </main>
  <footer>
    <p>&copy; 2024 Fabro Ludovic Portfolio</p>
  </footer>
  <script type="text/javascript" src="https://cdn.emailjs.com/dist/email.min.js"></script>
  <script src={ROUTES.SCRIPT}></script>
</body>
  );
}

export default CONTACT;
