import React from 'react';

const Layout = ({ children }) => {
  return (
    <div className="bg-[#f4f4f4] text-[#333]">
      {children}
    </div>
  );
};
const Header = ({ children }) => {
  return (
    <div className="bg-white text-center border-b border-solid border-gray-300 ">
      {children}
    </div>
  );
};
const Nav = ({ children }) => {
  return (
    <div className="py-4 px-6 text-sm font-medium">
      <ul className="flex items-stretch ">
        {children}
      </ul>
    </div>
  );
};
const NavItem = ({ href, isActive, children }) => {
  return (
    <li>
      <a
        href={href}
        className={`block px-3 py-2 rounded-md ${isActive ? 'bg-sky-500 text-white' : 'bg-slate-50'}`}
      >
        {children}
      </a>
    </li>
  );
};
export { Layout, Header,Nav, NavItem};
